        addWishList.style.display = "flex"
    }else{
        removeWishList.style.display = "flex"
        addWishList.style.display = "none"
    }

});

const observerCallback = (entries, observer) => {
  entries.forEach(async entry => {
    if (entry.isIntersecting) {
      await loadMoreContent(observer);
      observer.observe(loading);
    }
  });
};
const observer = new IntersectionObserver(observerCallback, {
  root: null, 
  threshold: 1.0 
});

observer.observe(loading);


 function loadMoreContent(observer){
       pageno += 1
        var category_route =""
        var url_params = window.location.href.split('/')[window.location.href.split('/').length-1];
        var brands = "";
        var attributes = [];
        if(url_params.indexOf("?")>-1){
            category_route = url_params.split('?')[0];
            var other_params = url_params.split('?')[1].split('&');
            console.log(other_params);
            if(other_params.length>0){
                for(var i=0;i<other_params.length;i++){
                    var params = other_params[i].split('=');
                    if(params[0]=="brands"){
                        brands = params[1];
                    }
                    if(params[0]!="brands" && params[0]!="page_no" && params[0]!="page_size" && params[0]!="sortby"){
                        attributes.push({"attribute":params[0],'value':params[1]});
                    }
                }
            }
        }
        else{
           category_route = url_params; 
        }
        if(window.innerWidth < 567){
        mobile_load_scroll_data(category_route,brands,attributes,pageno,observer);

        }else{
        load_scroll_data(category_route,brands,attributes,pageno,observer)
        }
}

