function setCategoryRoute(){
      let splitPath =paths.split("/")
     let lastIndex=splitPath.findLastIndex(e => e !== undefined);
     console.log(splitPath)
     for (let i = 0; i < subchild.length; i++) {
         let categoryroute =document.getElementsByClassName("category-route")[i]
         let routeValue =categoryroute.innerHTML
         if(splitPath[lastIndex] == routeValue || splitPath[lastIndex].includes(routeValue) ){
          subchild[i].style.display="flex"
          let subSubChildren = subchild[i].getElementsByClassName("sub_sub_child");
          for(let j = 0;j <subSubChildren.length;j++ ){
          let subcategoryroute =document.getElementsByClassName("sub-category-route")[j].innerHTML
          if(splitPath[lastIndex] == subcategoryroute || splitPath[lastIndex].includes(subcategoryroute) ){
              subSubChildren[0].style.display="flex"
          }else{
               subSubChildren[0].style.display="none"
          }
          } 
          break;
     }}
}
