function updateURLParams(url, newParams) {
    let urlObj = new URL(url);
    let searchParams = new URLSearchParams(urlObj.search);
    for (let [key, value] of Object.entries(newParams)) {
        key = key.toLowerCase();
        if (value === null) {
            searchParams.delete(key);
        } else if (searchParams.has(key)) {
            let existingValues = searchParams.get(key).split(',');
            if(searchParams.has('sortby')){
                searchParams.set('sortby', value);
            }else{
            if (existingValues.includes(value)) {
                existingValues = existingValues.filter(v => v !== value);
                if (existingValues.length > 0) {
                    searchParams.set(key, existingValues.join(','));
                } else {
                    searchParams.delete(key);
                }
            } else {
                existingValues.push(value);
                searchParams.set(key.toLowerCase(), existingValues.join(','));
            }
            }
        } else {
            if(key == "sort"){
                 searchParams.append(key.toLowerCase(), value);
            }else{
                searchParams.append(key.toLowerCase(), value);
            }
        }
    }
    urlObj.search = searchParams.toString();
  window.location.href = decodeURIComponent(urlObj.toString());
}

function checkAttributes(event){
    let attributesObj={}
    let parentNode=event.parentNode.parentNode.parentNode.childNodes[0].childNodes[0].innerText;
    let value = event.parentNode.getAttribute("data-route");
     if (event) {
        let target = event;
        if (target.classList.contains('myCheck')) {
            let checked = target.checked;
            if (!checked) {
                attributesObj[parentNode] = value; 
            } else {
                attributesObj[parentNode] = value;
            }
        }
    } else {
        console.error("Event or target is undefined.");
    }

    let url = window.location.href;
    updateURLParams(url, attributesObj);
}
