function removeFilter(event,key){
    let attributesObj={}
    let url = new URL(window.location.href);
    if(key == "all clear"){
     url.search = '';
     window.location.href = url.pathname;
    }else{
    const attributename = event.parentNode.getAttribute("data-attributename").replace(":","").trim().toLowerCase();
    const uniquename=event.getAttribute("data-uniquename")
       attributesObj[attributename]=uniquename
       let url = window.location.href;
       updateURLParams(url, attributesObj);
    }
}



