async function handleAddress(event,type) {
    let customerid = event.getAttribute("data-id");

    const getValue = (className) => document.getElementsByClassName(className)[0].value;
    const setError = (className, message) => {
        const element = document.getElementsByClassName(className)[0];
        element.classList.add("req_text");
        element.textContent = message;
    };

    let firstname = getValue("inp_firstname");
    let lastname = getValue("inp_lastname");
    let city = getValue("inp_city");
    let state = getValue("inp_state");
    let address = getValue("inp_address");
    let country = getValue("inp_country");
    let phone = getValue("inp_phone");
    let zipcode = getValue("inp_zipcode");
    let is_default = document.getElementById("myCheckbox").checked ? 1 : 0;

    if (firstname && lastname && city && state && address && country &&  zipcode.length === 6 && phone.length === 10){
        let obj={
            first_name:firstname,
            last_name:lastname,
            address:address,
            addr1:address,
            state:state,
            city:city,
            pincode:zipcode,
            country:country,
            phone:phone,
            is_default:is_default,
            customer:customerid,
            zipcode:zipcode
        }
        if(type == "edit"){
            obj["name"]=document.getElementsByClassName("name_inp")[0].textContent
            obj["parent"] =customerid
             let resp = await postMethod("go1_commerce.go1_commerce.v2.customer.update_address",{data:JSON.stringify(obj)})
             if(resp && resp.message){
                 clearInput()
             }else{
                 console.log(res.message)
             }

        }else{
             let resp = await postMethod("go1_commerce.go1_commerce.v2.customer.insert_address",{data:JSON.stringify(obj)})
             if(resp && resp.message){
                 clearInput()
             }else{
                 console.log(res.message)
             }
        }
    } else {
        if (!firstname) setError("req_firstname", "First Name is required");
        if (!lastname) setError("req_lastname", "Last Name is required");
        if (!city) setError("req_city", "City is required");
        if (!state) setError("req_state", "State is required");
        if (!address) setError("req_address", "Address is required");
        if (!country) setError("req_country", "Country is required");

        if (!zipcode || zipcode.length !== 6) {
            setError("req_zipcode", !zipcode ? "Zip Code is required" : "Zip Code must be a 6-digit number");
        }

        if (!phone || phone.length !== 10) {
            setError("req_phone", !phone ? "Phone is required" : "Phone must be a 10-digit number");
        }
    }
}
