async function applyCoupon(event,type){
    document.getElementById("overlay").style.display="flex";
    let subtotal = document.getElementById("cart-sub-total").getAttribute("data-amount");
    let discount = document.getElementById("cart-discount").getAttribute("data-amount");
    // console.log(subtotal,"subtooijd")
    let number =Number(subtotal.replace(/,/g, '').split('.')[0])
    let userId = getCookieValue('customer_id');
    let couponText = event.parentNode.childNodes[0].value
    console.log(couponText,"text")
    let amount = document.getElementsByClassName("payable_amount")[0]
    let disText= document.getElementsByClassName("discount_txt")[0]
    let disPrice= document.getElementsByClassName("discount_price")[0]
    let cancelBtn = document.getElementsByClassName("cancel_btn")[0]
    let applyBtn = document.getElementsByClassName("apply_btn")[0]

    if(type == "apply"){

    let obj= {
    coupon_code: couponText,
    subtotal: number-discount,
    customer_id: userId,
    shipping_charges:parseFloat(document.getElementById("cart-delivery-charges").getAttribute("data-amount")),
    shipping_method: shippingmethod ? shippingmethod:""
     }

     let resp = await postMethod("go1_commerce.go1_commerce.v2.checkout.validate_coupon",obj)
     if(resp.message.status == "success"){
        couponcode = document.getElementsByClassName("coupon_inp")[0].value;
        if(resp.message.shipping_discount){
            document.getElementById("cart-discount-delivery-charges").innerHTML= resp.message.formatted_actual_shipping_charges;
            document.getElementById("cart-discount-delivery-charges").style.display='flex';
            document.getElementById("cart-discount-delivery-charges").setAttribute("data-amount",resp.message.actual_shipping_charges)
             document.getElementById("cart-delivery-charges").setAttribute("data-amount",resp.message.shipping_charges);
           document.getElementById("cart-delivery-charges").innerHTML = resp.message.formatted_shipping_charges;
           document.getElementById("cart-discount").setAttribute("is-delivery-dicount",1);
           document.getElementById("cart-discount").setAttribute("coupon-amount",resp.message.discount_amount);
           set_order_totals(0,1,1);

        }
        else{
           document.getElementById("cart-discount").setAttribute("is-delivery-dicount",0);
            document.getElementById("cart-discount").setAttribute("coupon-amount",resp.message.discount_amount);           
           set_order_totals(resp.message.discount_amount,0,1);
        }


     }else{
         show_bottom_message("Error",resp.message.message);
         document.getElementById("overlay").style.display="none";
     }
    }else{
         document.getElementById("overlay").style.display="flex";
         cancelBtn.style.display= "none"
         applyBtn.style.display= "flex"
         discountamount =""
         couponcode = ""
         document.getElementsByClassName("coupon_inp")[0].value = ""
         couponText =""
         if(document.getElementById("cart-discount").getAttribute("is-delivery-dicount")=="0"){
             let discount = parseFloat(document.getElementById("cart-discount").getAttribute("data-amount"))-parseFloat(document.getElementById("cart-discount").getAttribute("coupon-amount"));
             let total_obj = {
                    "order_subtotal":parseFloat(document.getElementById("cart-sub-total").getAttribute("data-amount")),
                    "discount":discount,
                    "customer_id":getCookieValue('customer_id'),
                    "shipping_charges":parseFloat(document.getElementById("cart-delivery-charges").getAttribute("data-amount")),
             }
             let total_resp = await postMethod("go1_commerce.go1_commerce.v2.checkout.get_checkout_totals",total_obj);
             if(total_resp){
                 document.getElementById("cart-discount").innerHTML= total_resp.message.formatted_discount_amount;
                 document.getElementById("cart-sub-total").innerHTML= total_resp.message.formatted_order_subtotal;
                 document.getElementById("cart-delivery-charges").innerHTML= total_resp.message.formatted_shipping_charges;
                 document.getElementById("cart-tax").innerHTML= total_resp.message.formatted_tax_amount;
                 document.getElementById("cart-total-amount").innerHTML= total_resp.message.formatted_total_amount;
                 document.getElementById("cart-discount").setAttribute("data-amount",total_resp.message.discount);
                 document.getElementById("cart-sub-total").setAttribute("data-amount",total_resp.message.order_subtotal);
                 document.getElementById("cart-delivery-charges").setAttribute("data-amount",total_resp.message.shipping_charges);
                 document.getElementById("cart-tax").setAttribute("data-amount",total_resp.message.tax);
                 document.getElementById("cart-total-amount").setAttribute("data-amount",total_resp.message.total_amount);
                 document.getElementById("cart-discount").setAttribute("coupon-amount",0);

             }
         }
         else{
             let discount = parseFloat(document.getElementById("cart-discount").getAttribute("data-amount"));
             let shipping_charges = parseFloat(document.getElementById("cart-delivery-charges").getAttribute("data-amount"))+parseFloat(document.getElementById("cart-discount").getAttribute("coupon-amount"));
