    var has_variants = e.getElementsByClassName("has-variants")[0].getAttribute("data-variants");
    var item_name = e.parentNode.parentNode.parentNode.getElementsByClassName("item-name")[0].innerText;
    if(has_variants == 1){
        console.log(e.parentNode.parentNode.parentNode.getAttribute("href"))
        window.location.href=e.parentNode.parentNode.parentNode.getAttribute("href");
    }
    else{
        if(e.getElementsByClassName("inventory-method")[0].getAttribute("data-inventory-method")=="Track Inventory"){
           var avail_stock = e.getAttribute("data-stock");
           console.log(avail_stock)
            if(avail_stock=='' || avail_stock==0){
                var msg= "No stock available for the item <br/><b>"+item_name+"</b>";
                show_bottom_message("Error",msg);
                return false;
            }
            add_to_cart(item_name,e.getElementsByClassName("item-code")[0].getAttribute("data-item"),"Shopping Cart",1,"","");
        }

    }
}

