// Write your script here
function categoryOpt(e){
    let parentRoute = e.getAttribute("data-route");
    let submenu = document.getElementsByClassName("cateSub");
    for(let i = 0; i < submenu.length; i++){
        let childRoute = submenu[i].getAttribute("data-route");
        if(parentRoute.includes(childRoute)){
            submenu[i].style.display = "flex"
            let subSubmenu = submenu[i].getElementsByClassName("cateSubSub");
            for(let j = 0; j < subSubmenu.length; j++){
                let subSubchildRoute = subSubmenu[j].getAttribute("data-route");

                if(subSubchildRoute == parentRoute){
                    if(subSubmenu[j].style.display == "flex"){
                        subSubmenu[j].style.display = "none";
                    } else {
                        subSubmenu[j].style.display = "flex";
                    }
                } else {
                    subSubmenu[j].style.display = "none";
                }

            }

        } else {
            submenu[i].style.display = "none";
            let subSubmenu = submenu[i].getElementsByClassName("cateSubSub");
            for(let j = 0; j < subSubmenu.length; j++){
                subSubmenu[j].style.display = "none";
            }
        }
    }
}

