async function postMethod(api, payload) {
    // console.log(domainName)
    let k = await fetch(domainName + "/api/method/" + api, {
        "body": JSON.stringify(payload),
        "method": "POST",
        "headers": {
            "Content-Type": "application/json",
            "X-Frappe-CSRF-Token": frappe.csrf_token
        }
    })
    let val = await k.json()
    return val
}

