async function confirmPwd(event){
    // event.preventDefault()
    let oldPwd = document.getElementsByClassName("oldpwd")[0]
    let newPwd = document.getElementsByClassName("newpwd")[0]
    let cofPwd = document.getElementsByClassName("cofpwd")[0]

    console.log(oldPwd.value,newPwd.value,cofPwd.value)
    if(oldPwd.value != "" && newPwd.value != ""  && cofPwd.value != "" ){

    if(oldPwd.value == newPwd.value){
        document.getElementsByClassName("validation_popup")[0].style.display = "flex"
        document.body.style.overflow = "hidden"
        document.getElementsByClassName("errorMsg")[0].textContent = "Old password and new password must be unique..!"
        return
