    if(newPwd.value != cofPwd.value){
         document.getElementsByClassName("validation_popup")[0].style.display = "flex"
         document.body.style.overflow = "hidden"
         document.getElementsByClassName("errorMsg")[0].textContent = "New password and confirm password are not matching..!"
         return
    }

    let obj = {
     key: "",
     old_password: oldPwd.value,
     user: "pandiaraj@tridotstech.com",
     new_password: cofPwd.value

    }

  let resp = await postMethod("go1_commerce.go1_commerce.v2.customer.update_password",obj)
    let res = await resp.message
