function getCookieValue(cookieName) {

    let cookies = document.cookie;


    let cookieArray = cookies.split('; ');

    for (let i = 0; i < cookieArray.length; i++) {
        let cookiePair = cookieArray[i].split('=');

        if (cookiePair[0] === cookieName) {
            return cookiePair[1];
        }
    }

    return null;
}





