      if(total >= 4 ){
      count[type] += 4;
      scrollBy[type] += 4;
      }else if(total < 4 && total > 0){
         count[type] += 1;
        scrollBy[type] += 1;
      }else{
         return; 
      }
     }else if(screen.width > 567 && screen.width < 1024){
      if(total >= 2 ){
      count[type] += 2;
      scrollBy[type] += 2;
      }else if(total < 2 && total > 0){
         count[type] += 1;
        scrollBy[type] += 1;
      }else{
         return; 
      }
     }else if (screen.width < 576) {
      if(total > 0){
