async function update_variant_price(){
    document.getElementById("overlay").style.display="flex";
    var product_id = document.getElementById("product-info").getAttribute("data-id");
    var product_attributes = document.querySelectorAll('.product-attribute');
    var attribute_ids = "";
    var to_update=false;
    product_attributes.forEach(pA => {
       if(pA.getAttribute("data-control-type")=='Radio Button List'){
        var radioButtons = pA.querySelectorAll('input[type="radio"]');
        radioButtons.forEach(radio => {
            if(radio.checked == true){
                attribute_ids+= radio.parentNode.getAttribute("data-option-name")+",";
            }
        });
        to_update = true;
       }
       if(pA.getAttribute("data-control-type")=='Checkbox List'){
        var radioButtons = pA.querySelectorAll('input[type="checkbox"]');
        radioButtons.forEach(radio => {
            if(radio.checked == true){
                attribute_ids+= radio.parentNode.getAttribute("data-option-name")+",";
            }
        });
        to_update = true;

       }
      if(pA.getAttribute("data-control-type")=='Dropdown List'){
        var option_selected = pA.querySelector("select").selectedIndex;
        var option_id =  pA.getElementsByClassName("option-ids")[0].childNodes[option_selected].innerText;
         attribute_ids+= option_id+",";
        to_update = true;

       }
    });
    if(to_update){
         let customer_id = getCookie_by_id('customer_id');
            var params = JSON.stringify({
                "product":product_id,
                "attribute_id":attribute_ids.replace(",","
"),
                "variant_html":"",
                "cart_qty":1,
                "customer":customer_id
            });
           await fetch("/api/method/go1_commerce.go1_commerce.v2.orders.validate_attributes_stock", {
                  method: "POST",
                  body:params,
                  headers:det_req_headers
                }).then(async function (res){
                    let val = await res.json();
                    if(val.message){
                        var resp = val.message;
                        if(!resp.old_price>0){
                             document.getElementById("old-price-text").style.display = "none";
                        }
                        if(resp.old_price>0){
                             document.getElementById("old-price-text").style.display = "flex";
                        }
                        if(resp.status=="Success"){
                            document.getElementById("price-text").innerHTML = resp.formatted_price;
                            document.getElementById("old-price-text").innerHTML = resp.formatted_old_price;
                            if(resp.in_cart==1){
                                document.getElementById("cart-qty-div").childNodes[0].setAttribute("data-item-id",resp.cart_item_id);
                                document.getElementById("cart-qty-div").childNodes[1].innerHTML=resp.in_cart_qty;
                                document.getElementById("cart-qty-div").childNodes[2].setAttribute("data-item-id",resp.cart_item_id);
                                document.getElementById("cart-qty-div").style.display="flex";
                                document.getElementById("add-cart-div").style.display="none";
                            }
                            else{
                                 document.getElementById("cart-qty-div").style.display="none";
                                document.getElementById("add-cart-div").style.display="flex";
                                document.getElementById("add-cart-div").innerHTML = "<p>Add to cart</p>";
                                document.getElementById("add-cart-div").setAttribute("onclick","product_add_to_cart(this)");
                            }

                        }
                        else{
                            document.getElementById("cart-qty-div").style.display="none";
                            document.getElementById("add-cart-div").style.display="flex";
                            document.getElementById("price-text").innerHTML = resp.formatted_price;
                            document.getElementById("old-price-text").innerHTML = resp.formatted_old_price;
                            document.getElementById("add-cart-div").innerHTML = "<p>Out Of Stock</p>";
                            document.getElementById("add-cart-div").removeAttribute("onclick");
                            // onclick="product_add_to_cart(this)"
                        }
                        document.getElementById("overlay").style.display="none";
                    }
                    else{
                        document.getElementById("overlay").style.display="none";
                    }
                });
    }
    else{
        document.getElementById("overlay").style.display="none";

    }

}
function getCookie_by_id(cname) {
  let name = cname + "=";
  let decodedCookie = decodeURIComponent(document.cookie);
  let ca = decodedCookie.split(';');
  for(let i = 0; i <ca.length; i++) {
    let c = ca[i];
    while (c.charAt(0) == ' ') {
      c = c.substring(1);
    }
    if (c.indexOf(name) == 0) {
      return c.substring(name.length, c.length);
    }
  }
  return "";
}
