function validate_variants(){
    var attribute_ids = "";
    var attribute_html = "";
    var product_attributes = document.querySelectorAll('.product-attribute');
    var attribute_ids = "";
    product_attributes.forEach(pA => {
       var is_valid = false;
       if(pA.getAttribute("data-control-type")=='Radio Button List'){
            if(pA.childNodes[0].getAttribute("data-required")=="Yes")
            {
                var radioButtons = pA.querySelectorAll('input[type="radio"]');
                radioButtons.forEach(radio => {
                    if(radio.checked == true){
                        attribute_ids+= radio.parentNode.getAttribute("data-option-name")+",";
                        attribute_html+='<span class="attr-title">' + pA.querySelector(".attribute-title").innerText + '</span> : <span>' + radio.parentNode.innerText + '</span><br>';
                        is_valid = true;
                    }
                });
            }
           if(is_valid==false){
                show_bottom_message("Error","Please select any one option of <b>"+pA.querySelector(".attribute-title").innerText+"</b> ");
               return {"attribute_ids":attribute_ids,"attribute_html":attribute_html,"status":"Failed"};
           }
       }
       if(pA.getAttribute("data-control-type")=='Checkbox List'){
            if(pA.childNodes[0].getAttribute("data-required")=="Yes")
            {
                var radioButtons = pA.querySelectorAll('input[type="checkbox"]');
                radioButtons.forEach(radio => {
                    if(radio.checked == true){
                        attribute_ids+= radio.parentNode.getAttribute("data-option-name")+",";
                        attribute_html+='<span class="attr-title">' + pA.querySelector(".attribute-title").innerText + '</span> : <span>' + radio.parentNode.innerText + '</span><br>';

                        is_valid = true;
                    }
                });
            }
           if(is_valid==false){
                show_bottom_message("Error","Please select any one option of <b>"+pA.querySelector(".attribute-title").innerText+"</b> ");
               return {"attribute_ids":attribute_ids,"attribute_html":attribute_html,"status":"Failed"};
           }
       }
       if(pA.getAttribute("data-control-type")=='Dropdown List'){
            var option_selected = pA.querySelector("select").selectedIndex;
            var option_id =  pA.getElementsByClassName("option-ids")[0].childNodes[option_selected].innerText;
             attribute_ids+= option_id+",";
             attribute_html+='<span class="attr-title">' + pA.querySelector(".attribute-title").innerText + '</span> : <span>' + pA.querySelector("select").value + '</span><br>';

            to_update = true;

       }
        if(pA.getAttribute("data-control-type")=='Text Box'){
            var option_selected = pA.querySelector("input").value;
            if(option_selected==""){
                show_bottom_message("Error","Please enter the value for <b>"+pA.querySelector(".attribute-title").innerText+"</b> ");
               return {"attribute_ids":attribute_ids,"attribute_html":attribute_html,"status":"Failed"};
            }
            else{
                attribute_html+='<span class="attr-title">' + pA.querySelector(".attribute-title").innerText + '</span> : <span>' + pA.querySelector("input").value + '</span><br>';

            }

       }
         if(pA.getAttribute("data-control-type")=='Multi Line Text'){
            var option_selected = pA.querySelector("textarea").value;
            if(option_selected==""){
                show_bottom_message("Error","Please enter the value for <b>"+pA.querySelector(".attribute-title").innerText+"</b> ");
               return {"attribute_ids":attribute_ids,"attribute_html":attribute_html,"status":"Failed"};
            }
            else{
                attribute_html+='<span class="attr-title">' + pA.querySelector(".attribute-title").innerText + '</span> : <span>' + pA.querySelector("textarea").value + '</span><br>';

            }

       }
    });
    return {"attribute_ids":attribute_ids.replace(",","
"),"attribute_html":attribute_html,"status":"Success"};
}

