        console.log()
        if(attr_valid_resp.status=="Success"){
            add_to_cart(item_name,document.getElementById("product-info").getAttribute("data-id"),"Shopping Cart",1,attr_valid_resp.attribute_html,attr_valid_resp.attribute_ids,"detail");

        }
