async function update_cart_html(cart_type){
    var customer = getCookie("customer_id");
    await fetch("/api/method/go1_commerce.go1_commerce.v2.builder_page.get_mini_cart_html", {
      method: "POST",
      body: JSON.stringify({
        cart_type: cart_type,
        customer: customer,
      }),
      headers: {
        "Content-type": "application/json; charset=UTF-8"
      }
    }).then(async function (res){
        let val = await res.json();
        if(val.message) {
            if(val.message.status=="Success"){
                if(cart_type=="Shopping Cart"){
                    document.getElementById("mini-shopping-cart").innerHTML = val.message.cart_html;
                    document.getElementById("mini-cart-total").innerHTML = "Total : "+val.message.total_amount;
                    document.getElementById("mini-shopping-cart").removeAttribute("style");
                }
                else{
                    document.getElementById("mini-wishlist").innerHTML = val.message.cart_html;
                    document.getElementById("mini-wishlist").removeAttribute("style");
                }

            }
            else{
                if(val.message.message=="You are not a valid Customer.")
                {
                    window.location.href="/login";

                }
                else{
                     show_bottom_message("Error","Something went worng !");

                }
            }

        }
        else {
           show_bottom_message("Error","Something went worng !");
        }

    }).catch((error)=>{
        show_bottom_message("Error","Something went worng !");
    })
}
