html:
{% set defaults=frappe.get_doc('Catalog Settings','CAT-S-00001') %}
{% set theme_settings=frappe.get_doc('Theme Settings','Theme Settings') %}
{% if defaults.default_currency %}
{% set currency=frappe.db.get_value('Currency',defaults.default_currency,'symbol')%}
{% else %}
{% set currency='' %}
{% endif %}
{% set catalog_settings=frappe.get_doc('Catalog Settings','CAT-S-00001') %}
{% set business = frappe.get_doc('Business',doc.business) %}
{% set slots=doc.get_delivery_timeslot(doc.order_reference) %}
<div class="p-format">
    <div>
        <div>
            <div class="row">
                <div style="text-align:center;">
                    <h4>Tax Invoice</h4>
                </div>
            </div>
             <div class="row">
<div style="float: left;width: 100%;border: 1px solid #d1d8dd;border-bottom: none; ">
                <div class="col-md-6" style="float:left;width:50%">
                <div style="border-right: 1px solid #d1d8dd;padding: 10px 0;">
                    <h4 style=" margin-bottom: 0;margin-top:0;font-size: 13px; ">Address</h4>
                    <div style="padding-left:10px;padding-top: 10px;">
                    <!-- <h4 style=" margin-bottom: 0;margin-top:0;font-size: 13px; ">Bibleshoppy</h4>-->
                    <!-- <p>Pauls Techno Crafts Pvt Ltd,</p>-->
                    <!--<p>Plot No 166,</p>-->
                    <!--<p>Suraksha Enclave,</p>-->
                    <!--<p>Telangana - 500050.</p>-->
                </div>
                </div>
               
</div>
   <div class="col-md-6" style="padding-top: 42px;float:left;width:50%">
    <div class="row">
                        <div class="cols-3" style="padding: 3px;"> <b>Invoice No: </b> </div>
                        <div class="cols-6" style="padding: 3px;"><span>{{doc.order_reference}}</span><br></div>
                    </div>
<div class="row">
                        <div class="cols-3" style="padding: 3px;"> <b>Invoice Date: </b> </div>
                        <div class="cols-6" style="padding: 3px;"><span>{{doc.get_formatted("order_date")}}</span><br></div>
                    </div>
                
                 
    </div>
</div>
</div>

            <div class="row">
<div style="float: left;width: 100%;border: 1px solid #d1d8dd;border-bottom: none; ">
                <div class="col-md-6" style="float:left;width:50%">
<div style="
    border-right: 1px solid #d1d8dd;
    padding: 10px 0;
">
<h4 style=" margin-bottom: 0;margin-top:0;font-size: 13px; ">Sold By</h4>
                    <div  style="
    padding-left: 10px;
    padding-top: 5px;
">
 
                        <span style="margin-bottom: 3px;margin-top:0;font-weight: bold;float: left;width: 100%;">{{business.restaurant_name}}</span>
                        <p>
                            {% if business.business_address %}
                            {{business.business_address}} <br>
                            {% endif %}
                            {{business.city}}, {% if business.state %}{{business.state}}, {% endif %}{{business.country}}{% if business.zip_code%} - {{business.zip_code}}{% endif %}
                        </p>
                    </div>
                    {%if business.contact_number%}
<div class="row">
                        <div class="cols-5" style="padding: 3px;padding-left: 25px;"> <b>Contact Number : </b> </div>
                        <div class="cols-6" style="padding: 3px;"><span>{{business.contact_number}}</span><br></div>
                    </div>
 
{%endif%}
                </div></div>
                <div class="col-md-6"  style="float:left;width:50%">
 
                   <div style="
    padding: 10px 0;
">
{%if business.pan%}
<div class="row">
                        <div class="cols-3" style="padding: 3px;"> <b>Pan: </b> </div>
                        <div class="cols-6" style="padding: 3px;"><span>{{business.pan}}</span><br></div>
                    </div>
 
{%endif%}
{%if business.gstin%}
<div class="row">
                        <div class="cols-3" style="padding: 3px;"> <b>GSTIN : </b> </div>
                        <div class="cols-6" style="padding: 3px;"><span>{{business.gstin}}</span><br></div>
                    </div>
 
{%endif%}

<div class="row">
                        <div class="cols-3" style="padding: 3px;"> <b>Order No: </b> </div>
                        <div class="cols-6" style="padding: 3px;"><span>{{doc.order_reference}}</span><br></div>
                    </div>
<div class="row">
                        <div class="cols-3" style="padding: 3px;"> <b>Order Date: </b> </div>
                        <div class="cols-6" style="padding: 3px;"><span>{{doc.get_formatted("order_date")}}</span><br></div>
                    </div>
                 <div class="row">
                        <div class="cols-4" style="padding: 3px;"> <b>Shipping Method: </b> </div>
                        <div class="cols-6" style="padding: 3px;"><span>{{doc.shipping_method_name}}</span><br></div>
                    </div>
                 
                    <div class="row">
                        <div class="cols-4" style="padding: 3px;"> <b>Payment Method: </b> </div>
                        <div class="cols-6" style="padding: 3px;"><span>{{doc.payment_method_name}}</span><br></div>
                    </div>
                    </div>
                </div>
            </div>
</div>
        </div>
    </div>
    <div class="content" style="margin-top:-1px">
        <div class="row" style="border: 1px solid #d1d8dd;">
            <div class="cols-6" style="padding-bottom: 0;min-height: 140px;">
                <div style="border-right:1px solid #d1d8dd;padding-top: 10px;padding-bottom: 10px;">
                <h6 style="margin-left:10px;font-size: 13px;margin: 0;"><b>Billing Address</b></h6>
                <div style="margin-left: 20px;">
                    <p><b>{{doc.first_name}} {% if doc.last_name %}{{doc.last_name}}{% endif %}</b></p>
                    <div>
                        {{doc.address}},
                        <br>
                        {{doc.city}}<br>
                        {% if doc.state %}{{doc.state}} {% endif %}<br> {% if doc.country %}{{doc.country}}{% endif %} - {{doc.zipcode}}
                        <br>
                        <b>Phone : </b>{{doc.phone}}
                    </div>
                </div>
            </div>
            </div>
            <div class="cols-6" style="padding-left:0;">
                <h6 style="margin-left:10px;font-size:13px"><b>Shipping Address</b></h6>
                <div style="margin-left: 20px;">
                    <p><b> {{doc.shipping_first_name}} {% if doc.shipping_last_name %}{{doc.shipping_last_name}}{% endif %}</b></p>
                    <div>{{doc.shipping_shipping_address}}
                        <br>
                        {{doc.shipping_city}}<br>
                        {% if doc.shipping_state %}{{doc.shipping_state}} {% endif %}<br> {% if doc.shipping_country %}{{doc.shipping_country}}{% endif %} - {{doc.shipping_zipcode}}
                        <br>
                        <b>Phone : </b>{{doc.shipping_phone}}
                    </div>
                </div>
            </div>
            
        </div>
    </div>
    {% if slots %}
                <div class="row">
                    <div class="col-md-12" style="width: 100%;border: 1px solid #d1d8dd;
    border-bottom: none;padding: 0px;border-top: 0px;">
                        
                        <table style="width: 100%;">
                            <thead><tr><th style="padding: 5px 15px;"><b>{{_("Preferred Delivery Slot")}}:</b></th></tr></thead>
                            <tbody>
                                <tr>                
                                </tr>
                                {% for slot in slots %}
                                <tr>
                                    <td style="padding: 10px 6px !important;">{% if slot.category %}{{slot.category}} - {% endif %}{{slot.delivery_date}} ({{slot.from_time}})</td>
                                </tr>
                                {% endfor %}
                            </tbody>
                        </table>
                        
                    </div>
                </div>
                {% endif %}
    <div class="row" style="margin-right:-16px">
    <table class="table" style="margin:0;">
        <thead>
            <tr>
                <th >S.No</th>
                <!-- <th>Item Code</th> -->
                <th>Item</th>
                <th>Price Amount</th>
                <th>Qty</th>
                <th>Discount</th>
                <th>Taxable Amount</th>
                {% if doc.total_tax_amount > 0%}
                {% if (business.state | str) != doc.state %}
                <th>IGST(%)</th>
                <th>IGST</th>
                {% else %}
                <th>CGST(%)</th>
                <th>CGST</th>
                <th>SGST(%)</th>
                <th>SGST</th>
                {% endif %}
                {% endif %}
                <th style="border-right: 1px solid #d1d8dd;">Total</th>
            </tr>
        </thead>
        <tbody style="border-left: 1px solid #d1d8dd">           
            {% if doc.order_item %}
            {% for item in doc.order_item %}
            <tr style="border-bottom: 1px solid #d1d8dd !important;">
                <td >{{loop.index}}</td>
                <td>
                    {{_(item.item_name)}}
                    {% if item.attribute_description %}<br>{{item.attribute_description}}{% endif %}
                </td>
                <td>{% if currency %}{{currency}}{% else %}${% endif %}{{"%0.2f" % item.price | float}}</td>
                <td>{{item.quantity}}</td>
                
                <td>{% if currency %}{{currency}}{% else %}${% endif %}{{"%0.2f" % (item.d_amount) | float}}</td>
                <td>{% if currency %}{{currency}}{% else %}${% endif %}{{"%0.2f" % (item.tax)| float}}</td>
                {% if doc.total_tax_amount > 0%}
                {% if (business.state | str) != doc.state %}
                <td>
                    {%set item_doc = frappe.get_doc("Product",item.item)%}
                         {%if item_doc.tax_category%}
                         {%set item_tax = frappe.db.get_all("Tax Rates",filters={"parent":item_doc.tax_category},fields=['*'])%}
                         {%if item_tax%}
                         {{"%.2f" % item_tax[0].rate | float}}%
                         {%else%}
                            0.00%
                         {%endif%}
                         {%else%}
                         0.00%
                         {%endif%}
                </td>
                
                
                 <td>{% if currency %}{{currency}}{% else %}${% endif %}{{"%.2f" % item.tax | float}}
                </td>
           
            {% else %}
             {% set tax_percent = 0 %}
            {%set item_doc = frappe.get_doc("Product",item.item)%}
             {%if item_doc.tax_category%}
             {%set item_tax = frappe.db.get_all("Tax Rates",filters={"parent":item_doc.tax_category},fields=['*'])%}
             {%if item_tax%}
                {% set tax_percent = item_tax[0].rate %}
             {%endif%}
            
             {%endif%}
            {% set tax_amount = item.tax/2 %}
            {% set tax_percent = tax_percent/2 %}
            <td>{{"%.2f" % tax_percent | float}}%</td>
            <td>{% if currency %}{{currency}}{% else %}${% endif %}{{"%.2f" % tax_amount | float}}</td>
            <td>{{"%.2f" % tax_percent | float}}%</td>
            <td>{% if currency %}{{currency}}{% else %}${% endif %}{{"%.2f" % tax_amount | float}}</td>
            
            {% endif %}
            {% endif %}
                
                <td style="border-right: 1px solid #d1d8dd;">{% if currency %}{{currency}}{% else %}${% endif %}{{"%0.2f" % item.t_amount | float}}</td>
            </tr>
            {% endfor %}
            {% endif %}
        </tbody>
        <tfoot style="border-left: 1px solid #d1d8dd;border-right: 1px solid #d1d8dd;">
            <tr>
                <td colspan="{% if doc.total_tax_amount > 0%}6{% else %}2{% endif %}"></td>
                <td colspan="6"><b>Sub Total: <span style="float:right;">{% if currency %}{{currency}}{% else %}${% endif %}{{"%.2f" % (doc.order_subtotal - doc.discount) | float}}</span></b></td>
            </tr>
              <tr>
                <td colspan="{% if doc.total_tax_amount > 0%}6{% else %}2{% endif %}"></td>
                <td colspan="6"><b>Tax: <span style="float:right;">{% if currency %}{{currency}}{% else %}${% endif %}{{"%.2f" % doc.total_tax_amount | float}}</span></b></td>
            </tr>
            <tr>
                <td colspan="{% if doc.total_tax_amount > 0%}6{% else %}2{% endif %}" style="border-top:none;"></td>
                <td colspan="6" style="border-top:none;"><b>Shipping: <span style="float:right;">{% if currency %}{{currency}}{% else %}${% endif %}{{"%.2f" % doc.shipping_charges | float}}</span></b></td>
            </tr>
            
            
            <tr>
                <td colspan="{% if doc.total_tax_amount > 0%}6{% else %}2{% endif %}" style="border-top:none;border-bottom: 1px solid #d1d8dc;"></td>
                <td colspan="6" style="border-top:none;border-bottom: 1px solid #d1d8dc;"><b>Order Total: <span style="float:right;">{% if currency %}{{currency}}{% else %}${% endif %}{{"%.2f" % doc.total_amount | float}}</span></b></td>
            </tr>
            <tr>
                <td colspan="1" style="border-top:none;border-bottom: 1px solid #d1d8dc;"><div>
        <p style="font-size: 13px;font-weight: 600;
    margin-top: 11px; ">Ordered Through </p></div></td>
    <td colspan="10" style="border-top:none;border-bottom: 1px solid #d1d8dc;border-right: 1px solid #d1d8dc;">
        <div><img class="img-responsive" src="{{catalog_settings.website_logo}}" alt="website-logo" style="width: 185px !important;"></div></td>
            </tr>
        </tfoot>
    </table>
</div>
    {% if business.authorised_signature %}
<div class="row" style="margin-right:-18px">
    <table style="width: 100%; ">
        <tr>
            <td style="width: 70%;"></td>
            <td style="width: 30%;text-align: center;" align="right">
                <p>{{business.restaurant_name}}</p>
                <img src="{{business.authorised_signature}}" style="max-height: 80px;width:auto !Important;max-width:100%">
                <p>Authorized Signatory</p>
            </td>
        </tr>
    </table>
</div>
    {% endif %}
    <table style="width:100%;">
        {% if doc.customer_comments %}
        <tr style="border-bottom: 1px solid #333;border-top: 1px solid #333;">
            <td><b>Special Instructions:</b> {{doc.customer_comments}} </td>
        </tr>
        {%endif%}
    </table>
    
</div>
<style>
.cols-4 {
    width: 33.33%;
}

.cols-3 {
    width: 25%;
}

.cols-5 {
    width: 41.67%;
}

.cols-8 {
    width: 66.67%;
}

.cols-6 {
    width: 50%;
}

.cols-2 {
    width: 20%;
}

.cols-10 {
    width: 80%;
}

.cols-4,
.cols-3,
.cols-5,
.cols-8,
.cols-6,
.cols-2,
.cols-10 {
    position: relative;
    min-height: 1px;
    padding-right: 15px;
    padding-left: 15px;
    float: left;
}

/*.p-format{
        border: 1px solid #ced1d4;
    }*/
.p-format .img-style {
    height: 100px;
    margin: 10px 0 0 0;
}

.p-format .content {
    margin-top: 10px;
}

/*.p-format .table thead{
        border-top: 1px solid #ced1d4;
    }*/
.table>tbody>tr>td {
    border-top: 0px !important;
    border-bottom: 1px solid #d1d8dd !important;
}

.print-format {
    padding: 0.50in !important;
}

.p-format th {
    background-color: #eaeaea !important;
}

.p-format .table>thead>tr>th,
.p-format .table>tfoot>tr>th,
.p-format .table>thead>tr>td,
.p-format .table>tbody>tr>td,
.p-format .table>tfoot>tr>td {
    /*border-right: 1px solid #ced1d4;*/
    border-top: 1px solid #ced1d4;
}

.p-format .table>thead>tr>th:last-child,
.p-format .table>tfoot>tr>th:last-child,
.p-format .table>thead>tr>td:last-child,
.p-format .table>tbody>tr>td:last-child,
.p-format .table>tfoot>tr>td:last-child {
    border-right: 0;
}

</style>
