html:
{% set defaults=frappe.get_doc('Catalog Settings','Catalog Settings') %}
{% if defaults.default_currency %}
{% set currency=frappe.db.get_value('Currency',defaults.default_currency,'symbol')%}
{% else %}
{% set currency='' %}
{% endif %}
{%set order_detail = frappe.get_doc('Order',doc.order_id)%}
{%set v_order_detail_list = frappe.get_all("Vendor Orders", fields = ['name'], filters = {'order_reference' : order_detail.name})%}
{%for item1 in v_order_detail_list%}
{%set order_detail_list = frappe.get_all("Order Item", fields = ['*'], filters = 
{'item':doc.product,'parent':item1.name})%}

{%if order_detail_list%}
<div style="width: 85%; margin: auto;">
<div style="background: #ed1c24; color: #fff; padding: 15px 20px; margin-bottom: 10px;">
<h1>Return Request</h1>
</div> 
<div class="order_box">
    <div class="row it-sec row-align">
        <div class="col-md-12 col-sm-12 col-xs-12" style="display:flex;">
            <div class="col-md-3 col-sm-3 col-xs-6">
                <h5><b>Posted Date</b></h5>
                <div>{{doc.posted_on}}</div>
            </div>
            <div class="col-md-3 col-sm-3 col-xs-6">
                <h5><b>Order #</b></h5>
                <div>{{doc.order_id}}</div>
            </div>
            <div class="col-md-3 col-sm-3 col-xs-6">
                <h5><b>Reason for return</b></h5>
                <div>{{doc.reason_for_return}}</div>
            </div>
            <div class="col-md-3 col-sm-3 col-xs-6">
                <h5><b>Requested Action</b></h5>
                <div>{{doc.requested_action}}</div>
            </div>
        </div>
    </div>
    <div class="row it-sec row-align">
        <div class="col-md-12 col-xs-12 col-sm-12">
            <div class="addrBox">
                <div class="row row-align">

                    <div class="col-md-4 col-xs-12 col-sm-4">
                        <h5><b>Billing Address</b></h5>
                        <div class="od-info">
                            {{order_detail.first_name}} {% if order_detail.last_name %}{{order_detail.last_name}}{% endif %}<br>
                            {{order_detail.address}}<br>
                            {{order_detail.city}}<br>
                            {{order_detail.state}} - {{order_detail.zipcode}}
                            {% if order_detail.country %}<br>{{order_detail.country}}{% endif %}
                        </div>
                        <div>
                            <p>Phone: {{order_detail.phone}}</p>
                        </div>
                    </div>

                    <div class="col-md-4 col-xs-12 col-sm-4">
                        <h5><b>Shipping Address</b></h5>
                        <div class="od-info">
                            {{order_detail.shipping_first_name}} {% if order_detail.shipping_last_name %}{{order_detail.shipping_last_name}}{% endif %}<br>
                            {{order_detail.shipping_shipping_address}}<br>
                            {{order_detail.shipping_city}}<br>
                            {{order_detail.shipping_state}} - {{order_detail.shipping_zipcode}}
                            {% if order_detail.shipping_country %}<br>{{order_detail.shipping_country}}{% endif %}
                        </div>
                        <div>
                            <p>Phone: {{order_detail.shipping_phone}}</p>
                        </div>
                    </div>

                    <div class="col-md-4 col-xs-12 col-sm-4">
                        {% if order_detail.shipping_method %}
                        <h5><b>Shipping Method</b></h5>
                        <div class="od-info">{{order_detail.shipping_method}}</div>
                        {% endif %}
                        {% if order_detail.payment_type %}
                        <h5><b>Payment Method</b></h5>
                        <div class="od-info">{{order_detail.payment_method}}</div>
                        {% endif %}
                    </div>
                </div>
            </div>

        </div>
    </div>
    {% if order_detail.customer_comments %}
    <div class="row it-sec  row-align">
        <div class="col-md-12 col-xs-12">
            <div class="addrBox">
                <h5><b>Special Instructions</b></h5>
                <div>{{order_detail.customer_comments}}</div>
            </div>
        </div>
    </div>
    {% endif %}

    {% if order_detail_list %}

    <div class="row it-sec  row-align">
        <div class="col-md-12 col-xs-12">
            <div class="it-box">
                <div class="item-table-head hidden-xs clearfix" style="display:flex;">
                    <div class="col-md-8 col-sm-7">Item</div>
                    <div class="col-md-4 col-sm-5" style="display:flex;">
                        <div class="col-md-4 col-sm-4">Price</div>
                        <div class="col-md-4 col-sm-4">Quantity</div>
                        <div class="col-md-4 ralign col-sm-4">Total</div>
                    </div>
                </div>
                <div class="item-details">

                    <div class="it-list">
                          {% for item in order_detail_list %}
                        <div class="row  row-align">
                            <div class="col-md-8 col-sm-7 col-xs-12" style="display:flex;">
                                <div class="col-md-2 col-xs-12 col-sm-3">
                    {% set image=order_detail.get_item_image(item.item) %}                              
                                    <img src="{% if image %}{{image}}{% else %}/assets/cmswebsite/images/no-img-120.jpg{% endif %}" class="img-responsive" style="margin: auto;" />

                                </div>
                                <div class="col-md-10 col-sm-9 col-xs-12">
                                    <h5 style="margin-top: 5px;">{{item.item_name}}</h5>
                                    {% if item.attribute_description %}
                                    <div class="attr-list">{{item.attribute_description}}</div>
                                    {% endif %}
                                </div>
                            </div>
                            <div class="col-md-4 col-sm-5 col-xs-12" style="display:flex">
                                <div class="col-md-4 col-sm-4 ralign">
                                    {% if currency %}{{currency}}{% else %}${% endif %}{{frappe.utils.fmt_money("%0.2f" % item.price | float)}} 
                                </div>
                                <div class="col-md-4 col-sm-4">
                                    {{doc.quantity}}
                                </div>
                                <div class="col-md-4 ralign col-sm-4">
                                    {% if currency %}{{currency}}{% else %}${% endif %}{{frappe.utils.fmt_money("%0.2f" % item.amount | float)}} 
                                </div>
                            </div>                            
                        </div>
                            {% endfor %}
                    </div>


                </div>
                <div class="item-footer">
                    <div class="row">
                        <div class="col-md-6 col-sm-6"></div>
                        <div class="col-md-6 col-sm-6">
                            <table>
                                {%for item in order_detail_list%}
                                <tr>
                                    <td>Sub Total:</td>
                                    <td class="ralign">{% if currency %}{{currency}}{% else %}${% endif %}{{frappe.utils.fmt_money("%0.2f" % (item.price * item.quantity) | float) }}</td>
                                </tr>
                             <!--    <tr>
                                    <td>Tax Charges {%if defaults.included_tax==1%}(Incl.tax){%endif%}:</td>
                                    <td class="ralign">{% if currency %}{{currency}}{% else %}${% endif %}{{frappe.utils.fmt_money("%0.2f" % item.total_tax_amount | float) }}</td>
                                </tr>  -->
                                <tr>
                                    <td>Shipping Charges:</td>
                                    <td class="ralign">{% if currency %}{{currency}}{% else %}${% endif %}{{frappe.utils.fmt_money("%0.2f" % item.shipping_charges | float) }}</td>
                                </tr>    
                {% if item.discount %} 
                                <tr>
                                    <td>Discount:</td>
                                    <td class="ralign">{% if currency %}{{currency}}{% else %}${% endif %}{{frappe.utils.fmt_money("%0.2f" % item.discount | float )}}</td>
                                </tr>
                {% endif %}                            
                                <tr>
                                    <td><b>Order Total:</b></td>
                                    <td class="ralign"><b>{% if currency %}{{currency}}{% else %}${% endif %}{{frappe.utils.fmt_money("%0.2f" % item.t_amount | float )}}</b></td>
                                </tr>
                                {%endfor%}
                            </table>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    </div>
    {% endif %}
</div></div>
{%endif%}
{%endfor%}
<style>
.addr-row{
    border-top: 1px solid #222;
    padding-top: 10px;
    margin-top: 10px;
margin-bottom: 10px;
}
.print-format th{
background:#fff !important;
}
.it-sec{
        margin-bottom: 20px;
    }
    .addrBox{
        background: #fff;
        padding: 10px 15px;
        border: 1px solid #ddd;
    }
    .item-table-head{
        border-bottom: 1px solid #ddd;
        padding: 10px;
    }
    .it-box{
        background: #fff;
        border: 1px solid #ddd;
    }
    .it-list{
        border-bottom: 1px solid #ddd;
        padding: 10px;
    }
    .item-footer table td{
        padding: 5px 15px;
    }
    .item-footer table{
        width: 100%;
    }
    .ralign{
        text-align: right;
    }
    .item-table-head div{
        font-weight: 600;
    }
    .hide-desk{
        display: none;
        margin-top: 10px;
    }
    .hide-desk div{
        font-size: 12px;
    }
    .hide-desk p{
        margin-bottom: 5px;
        font-weight: 600;
        font-size: 13px;
    }
    h5{
        margin-bottom: 15px;
    }
    .attr-list{
        font-size: 13px;
    }

.col-md-12 {
    width: 100%;
}
.col-md-3 {
    width: 25%;
}
.col-md-6 {
    width: 50%;
}
.col-md-9 {
    width: 75%;
}
.col-md-4 {
    width: 33.33333333%;
}
.col-md-2 {
    width: 16.66666667%;
}
.col-md-1 {
    width: 8.33333333%;
}
.col-md-5 {
    width: 41.66666667%;
}
.col-md-7 {
    width: 58.33333333%;
}
.col-md-10 {
    width: 83.33333333%;
}
.col-md-8{
width: 66.66666667%;
}
.col-md-1, .col-md-2, .col-md-3, .col-md-4, .col-md-5, .col-md-6, .col-md-7, .col-md-8, .col-md-9, .col-md-10, .col-md-11, .col-md-12{
    float:left;
position: relative;
    min-height: 1px;
    padding-right: 15px;
    padding-left: 15px;
}
.row-align{margin-right: -15px;
    margin-left: -15px;display:flex;}
</style>
