html:
{% if doc.currency %}
{% set currency=frappe.db.get_value('Currency',doc.currency,'symbol')%}
{% else %}
{% set currency='' %}
{% endif %}
{% set logo = '' %}
{% set business_detail = 0 %}
{% if doc.business %}
{% set settings = frappe.get_list('Catalog Settings', filters={'business': doc.business}, fields=['print_format_logo'], ignore_permissions=True)%}
{% if settings and settings[0].print_format_logo %}{% set logo = settings[0].print_format_logo %}{% endif %}
{% set business_info = frappe.get_list('Business', filters={'name': doc.business}, fields=['restaurant_name','business_address', 'country', 'city', 'state', 'zip_code', 'gstin', 'pan', 'cin', 'authorised_signature'], ignore_permissions=True) %}
{% if business_info %}{% set business_detail = 1 %}{% endif %}
{% endif %}
<div>
<div class="order_box p-format-order">
    <div class="row">
        <div style="text-align:center;">
            <h4>Tax Invoice</h4>
        </div>
    </div>
    <div class="row" style="border: 1px solid #d1d8dd;display: flex;">
            <div class="col-md-6" style="padding-left:0;width: 50%;">
                <div style="">
                    {% if logo %}<img class="img-responsive" src="{{logo}}" alt="website-logo" style="max-width: 65%;padding: 10px;">{% endif %}
                </div>
            </div>
            <div class="col-md-6" style="padding-bottom: 0;width: 50%;">
                <div style="padding-top: 10px;padding-bottom: 10px;">
                    <div style="padding-right: 10px;float: right;">
                        {% if business_detail %}
                        <p><b>{{ business_info[0].restaurant_name }}</b></p>
                        <div>
                            {% if business_info[0].business_address %}<p>{{business_info[0].business_address}}</p>{% endif %}
                            {% if business_info[0].city %}<p>{{business_info[0].city}}</p>{% endif %}
                            {% if business_info[0].state or business_info[0].zip_code %}
                            <p>{% if business_info[0].state %}{{business_info[0].state}}{% endif %} {% if business_info[0].zip_code %}{{business_info[0].zip_code}}{% endif %}</p>
                            {% endif %}
                            {% if business_info[0].gstin %}<p><b>GSTIN: </b>{{business_info[0].gstin}}</p>{% endif %}
                        </div>
                        {% endif %}
                    </div>
                </div>
            </div>
        </div>
        <div class="row" style="border: 1px solid #d1d8dd;display: flex;border-top: 0;border-bottom: 0;">
            <div class="col-md-6" style="padding-bottom: 0;border-right:1px solid #d1d8dd;width: 50%;">
                <div style="padding-top: 10px;padding-bottom: 10px;">
                    <h6 style="padding-left:10px;font-size: 13px;margin: 0;"><b>Billing Address</b></h6>
                    <div style="margin-left: 20px;">
                        <p><b>{{doc.first_name}} {% if doc.last_name %}{{doc.last_name}}{% endif %}</b></p>
                        <div>
                            {{doc.address}},
                            <br>
                            {{doc.city}}<br>
                            {% if doc.state %}{{doc.state}} {% endif %}<br> {% if doc.country %}{{doc.country}}{% endif %} - {{doc.zipcode}}
                            <br>
                            <b>Phone : </b>{{doc.phone}}
                        </div>
                    </div>
                </div>
            </div>
            <div class="col-md-6" style="width: 50%;">
                <h6 style="margin-left:10px;font-size:13px"><b>Shipping Address</b></h6>
                <div style="margin-left: 20px;">
                    <p><b> {{doc.shipping_first_name}} {% if doc.shipping_last_name %}{{doc.shipping_last_name}}{% endif %}</b></p>
                    <div>{{doc.shipping_shipping_address}}
                        <br>
                        {{doc.shipping_city}}<br>
                        {% if doc.shipping_state %}{{doc.shipping_state}} {% endif %}<br> {% if doc.shipping_country %}{{doc.shipping_country}}{% endif %} - {{doc.shipping_zipcode}}
                        <br>
                        <b>Phone : </b>{{doc.shipping_phone}}
                    </div>
                </div>
            </div>
        </div>
        <div class="row" style="margin-right:-16px">
        <table class="table" style="margin:0;">
            <thead>
                <tr style="border-left: 1px solid #d1d8dd; border-top: 1px solid #d1d8dd;">
                    <th style="border-right: 1px solid #d1d8dd;">S.No</th>
                    <!-- <th>Item Code</th> -->
                    <th style="border-right: 1px solid #d1d8dd;">Product</th>
                    <th style="border-right: 1px solid #d1d8dd;">Qty</th>
                    <th style="border-right: 1px solid #d1d8dd;">Tax</th>
                    <th style="border-right: 1px solid #d1d8dd;">Price</th>
                    <th style="border-right: 1px solid #d1d8dd;">Total</th>
                </tr>
            </thead>
            <tbody style="border-left: 1px solid #d1d8dd"> {% if doc.order_item %}
                {% for item in doc.order_item %}
                <tr>
                    <td style="border-right: 1px solid #d1d8dd;width: 6%;">{{loop.index}}</td>
                    <!--  <td></td> -->
                    <td style="width: 45%;border-right: 1px solid #d1d8dd;">{{_(item.item_name)}}</td>
                    <td style="border-right: 1px solid #d1d8dd;">{{item.quantity}}</td>
                    <td style="border-right: 1px solid #d1d8dd;">{% if currency %}{{currency}}{% else %}${% endif %}{{"%0.2f" % item.tax | float}}</td>
                    <td style="border-right: 1px solid #d1d8dd;">{% if currency %}{{currency}}{% else %}${% endif %}{{"%0.2f" % item.price | float}}</td>
                    <td style="border-right: 1px solid #d1d8dd;">{% if currency %}{{currency}}{% else %}${% endif %}{{"%0.2f" % item.amount | float}}</td>
                </tr>
                {% endfor %}
                {% endif %}
            </tbody>
            <tfoot style="border-left: 1px solid #d1d8dd;border-right: 1px solid #d1d8dd;">
                <tr>
                    <td colspan="3"></td>
                    <td colspan="4"><b>Sub Total: <span style="float:right;padding-right: 5px;">{% if currency %}{{currency}}{% else %}${% endif %}{{"%.2f" % doc.order_subtotal | float}}</span></b></td>
                </tr>
                <tr>
                    <td colspan="3" style="border-top:none;"></td>
                    <td colspan="4" style="border-top:none;"><b>Shipping: <span style="float:right;padding-right: 5px;">{% if currency %}{{currency}}{% else %}${% endif %}{{"%.2f" % doc.shipping_charges | float}}</span></b></td>
                </tr>
                <tr>
                    <td colspan="3" style="border-top:none;"></td>
                    <td colspan="4" style="border-top:none;"><b>Tax: <span style="float:right;padding-right: 5px;">{% if currency %}{{currency}}{% else %}${% endif %}{{"%.2f" % doc.total_tax_amount | float}}</span></b></td>
                </tr>
                <tr>
                    <td colspan="3" style="border-top:none;border-bottom: 1px solid #d1d8dc;"></td>
                    <td colspan="4" style="border-top:none;border-bottom: 1px solid #d1d8dc;"><b>Order Total: <span style="float:right;padding-right: 5px;">{% if currency %}{{currency}}{% else %}${% endif %}{{"%.2f" % doc.total_amount | float}}</span></b></td>
                </tr>
            </tfoot>
        </table>
    </div>
    {% if business_detail and business_info[0].authorised_signature %}
    <div class="row" style="margin-right:-18px">
        <table style="width: 100%; ">
            <tr>
                <td style="width: 70%;"></td>
                <td style="width: 30%;text-align: center;" align="right">
                    <p>{{business_info[0].restaurant_name}}</p>
                    <img src="{{business_info[0].authorised_signature}}" style="max-height: 80px;width:auto !Important;max-width:100%">
                    <p>Authorized Signatory</p>
                </td>
            </tr>
        </table>
    </div>
    {% endif %}
    </div>
</div>
<style>
.p-format-order .addr-row{
    border-top: 1px solid #222;
    padding-top: 10px;
    margin-top: 10px;
margin-bottom: 10px;
}
.p-format-order th{
/*background:#fff !important;*/
}
.p-format-order .it-sec{
        margin-bottom: 20px;
    }
    .p-format-order .it-box{
        background: #fff;
        border: 1px solid #ddd;
    }
    .p-format-order .it-list{
        border-bottom: 1px solid #ddd;
        padding: 10px;
    }
    .p-format-order .item-footer table td{
        padding: 5px 15px;
    }
    .p-format-order .item-footer table{
        width: 100%;
    }
    .p-format-order .ralign{
        text-align: right;
    }
    .p-format-order .hide-desk{
        display: none;
        margin-top: 10px;
    }
    .p-format-order .hide-desk div{
        font-size: 12px;
    }
    .p-format-order .hide-desk p{
        margin-bottom: 5px;
        font-weight: 600;
        font-size: 13px;
    }
    .p-format-order h5{
        margin-bottom: 15px;
    }
    .p-format-order .attr-list{
        font-size: 13px;
    }
.p-format .order_box .img-style {
    height: 100px;
    margin: 10px 0 0 0;
}

.p-format .order_box .content {
    margin-top: 10px;
}

/*.p-format .table thead{
        border-top: 1px solid #ced1d4;
    }*/
.order_box .table>tbody>tr>td {
    border-top: 0px !important;
    border-bottom: 1px solid #d1d8dd !important;
}

.print-format .order_box {
    padding: 0.50in !important;
}

.p-format .order_box th {
    background-color: #eaeaea !important;
}

.p-format .order_box .table>thead>tr>th,
.p-format .order_box .table>tfoot>tr>th,
.p-format .order_box .table>thead>tr>td,
.p-format .order_box .table>tbody>tr>td,
.p-format .order_box .table>tfoot>tr>td {
    /*border-right: 1px solid #ced1d4;*/
    border-top: 1px solid #ced1d4;
}

.p-format .order_box .table>thead>tr>th:last-child,
.p-format .order_box .table>tfoot>tr>th:last-child,
.p-format .order_box .table>thead>tr>td:last-child,
.p-format .order_box .table>tbody>tr>td:last-child,
.p-format .order_box .table>tfoot>tr>td:last-child {
    border-right: 0;
}
</style>
