html:
{% set defaults=frappe.get_doc('Catalog Settings','Catalog Settings') %}
{% if defaults.default_currency %}
{% set currency=frappe.db.get_value('Currency',defaults.default_currency,'symbol')%}
{% else %}
{% set currency='' %}
{% endif %}
<div style="width: 85%; margin: auto;">
<div style="background: #247eba; color: #fff; padding: 15px 20px; margin-bottom: 10px;">
<h1>Order Delivered</h1>
</div> 
<div class="order_box">
    <div class="row it-sec">
        <div style="display: flex;" class="col-md-12 col-sm-12 col-xs-12">
            <div class="col-md-3 col-sm-3 col-xs-6">
                <h5><b>Order Date</b></h5>
                <div>{{doc.order_date}}</div>
            </div>
            <div class="col-md-3 col-sm-3 col-xs-6">
                <h5><b>Order #</b></h5>
                <div>{{doc.order_reference}}</div>
            </div>
            <div class="col-md-3 col-sm-3 col-xs-6">
                <h5><b>Order Status</b></h5>
                <div>{{doc.status}}</div>
            </div>
            <div class="col-md-3 col-sm-3 col-xs-6">
                <h5><b>Order Total</b></h5>
                <div>{% if currency %}{{currency}}{% else %}${% endif %}{{"%0.2f" % doc.total_amount | float}}</div>
            </div>
        </div>
    </div>
    <div class="row it-sec">
        <div class="col-md-12 col-xs-12 col-sm-12">
            <div class="addrBox">
                <div class="row">
                    {% if doc.billing_address %}
                    <div class="col-md-4 col-xs-12 col-sm-4">
                        <h5><b>Billing Address</b></h5>
                        <div class="od-info">
                            {{doc.first_name}} {% if doc.last_name %}{{doc.last_name}}{% endif %}<br>
                            {{doc.address}}<br>
                            {{doc.city}}<br>
                            {{doc.state}} - {{doc.zipcode}}
                            {% if doc.country %}<br>{{doc.country}}{% endif %}
                        </div>
                        <div>
                            <p>Phone: {{doc.phone}}</p>
                        </div>
                    </div>
                    {% endif %}
                    {% if doc.shipping_address %}
                    <div class="col-md-4 col-xs-12 col-sm-4">
                        <h5><b>Shipping Address</b></h5>
                        <div class="od-info">
                            {{doc.shipping_first_name}} {% if doc.shipping_last_name %}{{doc.shipping_last_name}}{% endif %}<br>
                            {{doc.shipping_shipping_address}}<br>
                            {{doc.shipping_city}}<br>
                            {{doc.shipping_state}} - {{doc.shipping_zipcode}}
                            {% if doc.shipping_country %}<br>{{doc.shipping_country}}{% endif %}
                        </div>
                        <div>
                            <p>Phone: {{doc.shipping_phone}}</p>
                        </div>
                    </div>
                    {% endif %}
                    <div class="col-md-4 col-xs-12 col-sm-4">
                        {% if doc.shipping_method %}
                        <h5><b>Shipping Method</b></h5>
                        <div class="od-info">{{doc.shipping_method}}</div>
                        {% endif %}
                        {% if doc.payment_type %}
                        <h5><b>Payment Method</b></h5>
                        <div class="od-info">{{doc.payment_method}}</div>
                        {% endif %}
                    </div>
                </div>
            </div>

        </div>
    </div>
    {% if doc.customer_comments %}
    <div class="row it-sec">
        <div class="col-md-12 col-xs-12">
            <div class="addrBox">
                <h5><b>Special Instructions</b></h5>
                <div>{{doc.customer_comments}}</div>
            </div>
        </div>
    </div>
    {% endif %}
    {% if doc.order_item %}
    <div class="row it-sec">
        <div class="col-md-12 col-xs-12">
            <div class="it-box">
                <div style="display: flex;" class="item-table-head hidden-xs clearfix">
                    <div class="col-md-8 col-sm-7">Item</div>
                    <div style="display: flex;" class="col-md-4 col-sm-5">
                        <div class="col-md-4 col-sm-4">Price</div>
                        <div class="col-md-4 col-sm-4">Quantity</div>
                        <div class="col-md-4 ralign col-sm-4">Total</div>
                    </div>
                </div>
                <div class="item-details">
                    {% for item in doc.order_item %}
                    <div class="it-list">
                        <div class="row">
                            <div style="display: flex;" class="col-md-8 col-sm-7 col-xs-12">
                                <div class="col-md-2 col-xs-12 col-sm-3">
					{% set image=doc.get_item_image(item.item) %}                              
                                    <img src="{% if image %}{{image}}{% else %}/assets/cmswebsite/images/no-img-120.jpg{% endif %}" style="margin: auto;" class="img-responsive">

                                </div>
                                <div class="col-md-10 col-sm-9 col-xs-12">
                                    <h5 style="margin-top: 5px;">{{item.item_name}}</h5>
                                    {% if item.attribute_description %}
                                    <div class="attr-list">{{item.attribute_description}}</div>
                                    {% endif %}
                                </div>
                            </div>
                            <div style="display: flex;" class="col-md-4 col-sm-5 col-xs-12">
                                <div class="col-md-4 col-sm-4 ralign">
                                    {% if currency %}{{currency}}{% else %}${% endif %}{{"%0.2f" % item.price | float }}
                                </div>
                                <div class="col-md-4 col-sm-4">
                                    {{item.quantity}}
                                </div>
                                <div class="col-md-4 ralign col-sm-4">
                                    {% if currency %}{{currency}}{% else %}${% endif %}{{"%0.2f" % item.amount | float }}
                                </div>
                            </div>                            
                        </div>
                    </div>
                    {% endfor %}
                </div>
                <div class="item-footer">
                    <div class="row">
                        <div class="col-md-6 col-sm-6"></div>
                        <div class="col-md-6 col-sm-6">

				{% if doc.discount %} 

				{% endif %}                              
                                <table>
                                <tbody><tr>
                                    <td>Sub Total:</td>
                                    <td class="ralign">{% if currency %}{{currency}}{% else %}${% endif %}{{"%0.2f" % doc.order_subtotal | float }}</td>
                                </tr>
                                <tr>
                                    <td>Shipping Charges:</td>
                                    <td class="ralign">{% if currency %}{{currency}}{% else %}${% endif %}{{"%0.2f" % doc.shipping_charges | float }}</td>
                                </tr><tr>
                                    <td>Discount:</td>
                                    <td class="ralign">{% if currency %}{{currency}}{% else %}${% endif %}{{"%0.2f" % doc.discount | float }}</td>
                                </tr><tr>
                                    <td><b>Order Total:</b></td>
                                    <td class="ralign"><b>{% if currency %}{{currency}}{% else %}${% endif %}{{"%0.2f" % doc.total_amount | float }}</b></td>
                                </tr>
                            </tbody></table>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    </div>
    {% endif %}
</div></div>
<style>
.addr-row{
    border-top: 1px solid #222;
    padding-top: 10px;
    margin-top: 10px;
margin-bottom: 10px;
}
.print-format th{
background:#fff !important;
}
.it-sec{
        margin-bottom: 20px;
    }
    .addrBox{
        background: #fff;
        padding: 10px 15px;
        border: 1px solid #ddd;
    }
    .item-table-head{
        border-bottom: 1px solid #ddd;
        padding: 10px;
    }
    .it-box{
        background: #fff;
        border: 1px solid #ddd;
    }
    .it-list{
        border-bottom: 1px solid #ddd;
        padding: 10px;
    }
    .item-footer table td{
        padding: 5px 15px;
    }
    .item-footer table{
        width: 100%;
    }
    .ralign{
        text-align: right;
    }
    .item-table-head div{
        font-weight: 600;
    }
    .hide-desk{
        display: none;
        margin-top: 10px;
    }
    .hide-desk div{
        font-size: 12px;
    }
    .hide-desk p{
        margin-bottom: 5px;
        font-weight: 600;
        font-size: 13px;
    }
    h5{
        margin-bottom: 15px;
    }
    .attr-list{
        font-size: 13px;
    }
.row{margin-right: -15px;
    margin-left: -15px;display:flex;}
.col-md-12 {
    width: 100%;
}
.col-md-3 {
    width: 25%;
}
.col-md-6 {
    width: 50%;
}
.col-md-9 {
    width: 75%;
}
.col-md-4 {
    width: 33.33333333%;
}
.col-md-2 {
    width: 16.66666667%;
}
.col-md-1 {
    width: 8.33333333%;
}
.col-md-5 {
    width: 41.66666667%;
}
.col-md-7 {
    width: 58.33333333%;
}
.col-md-10 {
    width: 83.33333333%;
}
.col-md-8{
width: 66.66666667%;
}
.col-md-1, .col-md-2, .col-md-3, .col-md-4, .col-md-5, .col-md-6, .col-md-7, .col-md-8, .col-md-9, .col-md-10, .col-md-11, .col-md-12{
	float:left;
position: relative;
    min-height: 1px;
    padding-right: 15px;
    padding-left: 15px;
}
</style>
